import User from '../models/User.js';
import mongoose from 'mongoose';
// MongoDB URL
const dbURI = "mongodb://127.0.0.1:27017/yassine";




export const createUser = async (req, res) => {
  try {
    const { name, email } = req.body;
    if (!req.file) {
      return res.status(400).json({ error: 'No file uploaded' });
    }
    const photoId = req.file.id;

    const newUser = new User({
      name,
      email,
      photo: photoId
    });

    await newUser.save();

    res.status(201).json({ user: newUser });
  } catch (error) {
    console.error(error);
    res.status(500).json({ error: error.message });
  }
};

export const getUsers = async (req, res) => {
  try {
    const users = await User.find().populate('photo');
    res.status(200).json(users);
  } catch (error) {
    console.error(error);
    res.status(500).json({ error: error.message });
  }
};

export const getUserById = async (req, res) => {
  try {
    const { id } = req.params;
    const user = await User.findById(id).populate('photo');

    res.status(200).json(user);
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
};

export const deleteUser = async (req, res) => {
  try {
    const { id } = req.params;
    const user = await User.findById(id);
    if (!user) {
      return res.status(404).json({ error: 'User not found' });
    }
    // Delete the associated file and chunks
    const fileId = user.photo;
    await gfs.files.deleteOne({ _id: fileId });
    await mongoose.connection.db.collection('uploads.chunks').deleteMany({ files_id: fileId });

    //Delete user
    await User.findByIdAndDelete(id, req.body);

    res.status(200).json({ message: "User deleted successfully" });
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
};

export const updateUser = async (req, res) => {
  try {
    const { id } = req.params;
    const { name, email } = req.body;
    let photoId;

    // Find the existing user
    const user = await User.findById(id);
    if (!user) {
      return res.status(404).json({ error: 'User not found' });
    }

    // If a new file is uploaded, delete the old file and update with the new one
    if (req.file) {
      // Delete the old file
      const oldFileId = user.photo;
      await gfs.files.deleteOne({ _id: oldFileId });
      await mongoose.connection.db.collection('uploads.chunks').deleteMany({ files_id: oldFileId });

      // Save the new file ID
      photoId = req.file.id;
    } else {
      // Keep the existing photo ID if no new file is uploaded
      photoId = user.photo;
    }

    // Update the user's details
    user.name = name || user.name;
    user.email = email || user.email;
    user.photo = photoId;

    await user.save();

    res.status(200).json({ user });
  } catch (error) {
    console.error(error);
    res.status(500).json({ error: error.message });
  }
};

